// Get the runData object from the input item
const runData = items[0].json.data.resultData.runData;

// Prepare an array for the output
let results = [];

// Loop over each key (the node name) in the runData object
for (const name in runData) {
  // Check if the property belongs to the object
  if (runData.hasOwnProperty(name)) {
    // Get the first execution details for the node
    const execution = runData[name][0];

    // Create a new object with the desired format and add it to our results array
    if (execution) {
      results.push({
        nodeName: name,
        executionStatus: execution.executionStatus,
        executionIndex: execution.executionIndex,
      });
    }
  }
}

// Sort the array based on the executionIndex
results.sort((a, b) => a.executionIndex - b.executionIndex);

// Return a single item with the results nested under "runData"
return [{
  json: {
    runData: results
  }
}];
